import re

def caesar(word, shift):
        ct = "abcdefghijklmnopqrstuvwxyz" * 2
        rword = ""
        for l in word:
            rword += ct[ct.find(l)+shift]
        return rword

def break_caesar(message):
    punctuation = ".,!?"
    ts = [0] *26
    sm,smpos = 0,0
    wlist = re.sub(f"[{punctuation}]", "", message.lower()).split()
    for w in wlist:
         for s in range(26):
            if caesar(w,s) in WORDS:
                ts[s*-1] += 1
                if ts[s*-1] > sm:
                    sm = ts[s*-1]
                    smpos = s*-1
    return 26 + smpos

